import React, { useState, useRef } from 'react';
import { GoogleMap, Marker } from '@react-google-maps/api';
import useGoogleMapApi from '../../hooks/useGoogleMapAPI';
import nearbySearch from './nearbySearch';
import LocationButton from './LocationButton';

const containerStyle = {
  width: '100%',
  height: '100vh',
};

const center = {
  lat: -37.8136,
  lng: 144.9631,
};

const MapView = () => {
  const { isLoaded, loadError } = useGoogleMapApi();
  const mapRef = useRef(null);
  const [selectedPosition, setSelectedPosition] = useState(null);
  const [places, setPlaces] = useState([]);

  const handleMapLoad = (map) => {
    mapRef.current = map;
  };

  const handleMapClick = (event) => {
    const latLng = {
      lat: event.latLng.lat(),
      lng: event.latLng.lng(),
    };
    setSelectedPosition(latLng);
    setPlaces([]);
  };

  const handleSearchNearby = async () => {
    if (!selectedPosition) return;

    const { Place } = await window.google.maps.importLibrary('places');

    const request = {
      locationBias: {
        center: selectedPosition,
        radius: 800,
      },
      includedTypes: ['restaurant', 'cafe', 'convenience_store', 'supermarket'],
      maxResultCount: 20,
    };

    const results = await Place.searchNearby(request);
    setPlaces(results);
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading maps...</div>;

  return (
    <>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={14}
        onClick={handleMapClick}
        onLoad={handleMapLoad}
      >
        {selectedPosition && <Marker position={selectedPosition} />}

        {places.map((place, index) => (
          <Marker
            key={index}
            position={{
              lat: place.geometry.location.lat(),
              lng: place.geometry.location.lng(),
            }}
          />
        ))}
      </GoogleMap>

      {selectedPosition && <LocationButton onClick={handleSearchNearby} />}
    </>
  );
};

export default MapView;
