/* global google */
import ReactDOMServer from 'react-dom/server';
import CustomerMarker from '../components/Map/ CustomMarker';

function normalizeName(name) {
  return name.toLowerCase()
    .replace(/building\s*\d+/g, '')
    .replace(/academic|campus|centre|center|college|institute|school|university/g, '')
    .replace(/[^a-z0-9]/g, '')
    .trim();
}

function removeDuplicatePlacesByName(places) {
  const seen = new Set();
  return places.filter(place => {
    const norm = normalizeName(place.displayName);
    if ([...seen].some(existing => norm.includes(existing) || existing.includes(norm))) {
      return false;
    }
    seen.add(norm);
    return true;
  });
}

export async function searchNearbyAmenities({ map, clickedLocation, radius, includedPrimaryTypes, isMajorMode = false }) {
  const { Place, SearchNearbyRankPreference } = await google.maps.importLibrary('places');
  const { AdvancedMarkerElement } = await google.maps.importLibrary('marker');

  const CATEGORY_TYPE_MAP = {
    education: ['school', 'primary_school', 'secondary_school', 'university'],
    medical: ['hospital', 'doctor', 'pharmacy', 'dentist'],
    grocery: ['supermarket', 'grocery_store', 'department_store', 'shopping_mall'],
    transport: ['train_station', 'subway_station', 'bus_station', 'transit_station', 'light_rail_station'],
    green: ['park', 'state_park', 'campground', 'rv_park'],
    culture: ['museum', 'tourist_attraction', 'library', 'art_gallery', 'movie_theater', 'concert_hall', 'stadium'],
  };

  const categoryReverseLookup = {};
  for (const [category, types] of Object.entries(CATEGORY_TYPE_MAP)) {
    for (const type of types) {
      categoryReverseLookup[type] = category;
    }
  }

  if (!isMajorMode) {
    const request = {
      locationRestriction: {
        center: clickedLocation,
        radius,
      },
      fields: ['displayName', 'location', 'businessStatus', 'types', 'rating', 'userRatingCount'],
      includedPrimaryTypes,
      maxResultCount: 20,
      rankPreference: SearchNearbyRankPreference.POPULARITY,
      language: 'en-AU',
    };

    const { places = [] } = await Place.searchNearby(request);
    const filteredPlaces = places.filter(place => 
      place.businessStatus === 'OPERATIONAL' &&
      (place.userRatingCount ?? 0) >= 2 &&
      (place.rating ?? 0) >= 3.8
    );
    const uniquePlaces = removeDuplicatePlacesByName(filteredPlaces);
    uniquePlaces.forEach(place => {
      const matchedCategory = place.types?.map(t => categoryReverseLookup[t]).find(Boolean);
      place.category = matchedCategory || 'other';
    });

    const markers = uniquePlaces.map((place, index) => {
      const markerHTML = ReactDOMServer.renderToStaticMarkup(
        <CustomerMarker number={index + 1} />
      );
      const wrapper = document.createElement('div');
      wrapper.innerHTML = markerHTML;
      return new AdvancedMarkerElement({
        map,
        position: place.location,
        title: place.displayName,
        content: wrapper.firstChild,
      });
    });

    return { places: uniquePlaces, markers };
  }

  const results = [];

  async function fetchPlaces(primaryTypes) {
    const request = {
      locationRestriction: {
        center: clickedLocation,
        radius: 800,
      },
      fields: ['displayName', 'location', 'businessStatus', 'types', 'rating', 'userRatingCount'],
      includedPrimaryTypes: primaryTypes,
      maxResultCount: 20,
      rankPreference: SearchNearbyRankPreference.POPULARITY,
      language: 'en-AU',
    };
    const { places = [] } = await Place.searchNearby(request);
    return places.filter(place =>
      place.businessStatus === 'OPERATIONAL' &&
      (place.userRatingCount ?? 0) >= 2 &&
      (place.rating ?? 0) >= 3.8
    );
  }

  // 1. Education - include all matching
  const eduPlaces = await fetchPlaces(CATEGORY_TYPE_MAP.education);
  eduPlaces.forEach(place => place.category = 'education');
  results.push(...eduPlaces);

  // 2. Grocery - Coles, Woolworths, Aldi, + 1 Asian market + major center
  const groceryPlaces = await fetchPlaces(CATEGORY_TYPE_MAP.grocery);
  const grocerySelected = [];
  const nameCheck = name => name.toLowerCase();
  const addedChains = new Set();

  for (const place of groceryPlaces.sort((a, b) => a.location.lat - b.location.lat)) {
    const name = nameCheck(place.displayName);
    if ((/woolworths|coles|aldi/.test(name)) && ![...addedChains].some(c => name.includes(c))) {
      if (name.includes('woolworths')) addedChains.add('woolworths');
      if (name.includes('coles')) addedChains.add('coles');
      if (name.includes('aldi')) addedChains.add('aldi');
      place.category = 'grocery';
      grocerySelected.push(place);
    } else if (/asian|oriental/.test(name) && !grocerySelected.some(p => /asian|oriental/.test(p.displayName.toLowerCase()))) {
      place.category = 'grocery';
      grocerySelected.push(place);
    } else if (/mall|shopping|centre|center/.test(name)) {
      place.category = 'grocery';
      grocerySelected.push(place);
    }
    if (grocerySelected.length >= 5) break;
  }
  results.push(...grocerySelected);

  // 3. Transport - 1 each closest
  const transportPlaces = await fetchPlaces(CATEGORY_TYPE_MAP.transport);
  const nearestByType = {};
  for (const place of transportPlaces) {
    for (const type of place.types || []) {
      if (CATEGORY_TYPE_MAP.transport.includes(type) && !nearestByType[type]) {
        place.category = 'transport';
        nearestByType[type] = place;
        break;
      }
    }
  }
  results.push(...Object.values(nearestByType));

  // 4~7. Other categories max 2 by popularity
  const others = ['medical', 'green', 'culture'];
  for (const cat of others) {
    const othersPlaces = await fetchPlaces(CATEGORY_TYPE_MAP[cat]);
    const top2 = othersPlaces.sort((a, b) => (b.rating ?? 0) - (a.rating ?? 0)).slice(0, 2);
    top2.forEach(p => p.category = cat);
    results.push(...top2);
  }

  const uniqueResults = removeDuplicatePlacesByName(results);
  const fallbackCategorized = uniqueResults.map(place => {
    if (!place.category) {
      const matchedCategory = place.types?.map(t => categoryReverseLookup[t]).find(Boolean);
      return { ...place, category: matchedCategory || 'other' };
    }
    return place;
  });

  const markers = fallbackCategorized.map((place, index) => {
    const markerHTML = ReactDOMServer.renderToStaticMarkup(
      <CustomerMarker number={index + 1} />
    );
    const wrapper = document.createElement('div');
    wrapper.innerHTML = markerHTML;
    return new AdvancedMarkerElement({
      map,
      position: place.location,
      title: place.displayName,
      content: wrapper.firstChild,
    });
  });

  return { places: fallbackCategorized, markers };
}
