/* global google */
import ReactDOMServer from 'react-dom/server';
import CustomerMarker from '../components/Map/ CustomMarker';



function normalizeName(name) {
  return name.toLowerCase()
    .replace(/building\s*\d+/g, '')
    .replace(/academic|campus|centre|center|college|institute|school|university/g, '')
    .replace(/[^a-z0-9]/g, '')
    .trim();
}

function removeDuplicatePlacesByName(places) {
  const seen = new Set();
  return places.filter(place => {
    const norm = normalizeName(place.displayName);
    if ([...seen].some(existing => norm.includes(existing) || existing.includes(norm))) {
      return false;
    }
    seen.add(norm);
    return true;
  });
}

function toRad(deg) {
  return deg * Math.PI / 180;
}



export async function searchNearbyAmenities({ map, clickedLocation, radius, includedPrimaryTypes }) {
  const { Place, SearchNearbyRankPreference } = await google.maps.importLibrary('places');
  const { AdvancedMarkerElement } = await google.maps.importLibrary('marker');

  const CATEGORY_TYPE_MAP = {
    education: ['primary_school', 'secondary_school', 'university'],
    medical: ['hospital', 'doctor', 'clinic'],
    grocery: ['supermarket', 'grocery_store', 'department_store', 'shopping_mall', 'asian_grocery'],
    transport: ['train_station', 'subway_station', 'bus_station', 'transit_station', 'light_rail_station'],
    green: ['park', 'state_park', 'campground', 'rv_park'],
    culture: ['museum', 'library', 'art_gallery', 'stadium', 'concert_hall'],
  };

  const categoryReverseLookup = {};
  for (const [category, types] of Object.entries(CATEGORY_TYPE_MAP)) {
    for (const type of types) {
      categoryReverseLookup[type] = category;
    }
  }

  const request = {
    locationRestriction: {
      center: clickedLocation,
      radius,
    },
    fields: ['displayName', 'location', 'businessStatus', 'types', 'rating', 'userRatingCount'],
    includedPrimaryTypes,
    maxResultCount: 20,
    rankPreference: SearchNearbyRankPreference.DISTANCE,
    language: 'en-AU',
  };

  const { places = [] } = await Place.searchNearby(request);

  const categorizedPlaces = places.filter(place => place.businessStatus === 'OPERATIONAL').map(place => {
    const types = place.types ?? [];
    const category = types.map(t => categoryReverseLookup[t]).find(Boolean) || null;
    return { ...place, category };
  });

  const transportPlaces = categorizedPlaces
    .filter(p => p.category === 'transport')
    .slice(0, 20); // 순수 위치 기준 20개 제한

  const nonTransportPlaces = categorizedPlaces.filter(p => p.category !== 'transport').filter(place => {
    const rating = place.rating ?? 0;
    const reviewCount = place.userRatingCount ?? 0;

    if (reviewCount < 5 || rating < 3) return false;

    return true;
  });

  const nonTransportSorted = removeDuplicatePlacesByName(nonTransportPlaces)
    .sort((a, b) => (b.userRatingCount ?? 0) - (a.userRatingCount ?? 0));

  const finalPlaces = [...transportPlaces, ...nonTransportSorted];

  const markers = finalPlaces.map((place, index) => {
    const markerHTML = ReactDOMServer.renderToStaticMarkup(
      <CustomerMarker number={index + 1} />
    );
    const wrapper = document.createElement('div');
    wrapper.innerHTML = markerHTML;
    return new AdvancedMarkerElement({
      map,
      position: place.location,
      title: place.displayName,
      content: wrapper.firstChild,
    });
  });

  return { places: finalPlaces, markers };
}
