/* global google */
import ReactDOMServer from 'react-dom/server';
import CustomerMarker from '../components/Map/ CustomMarker';

export async function searchNearbyAmenities({ map, clickedLocation, radius, includedPrimaryTypes }) {
  const { Place, SearchNearbyRankPreference } = await google.maps.importLibrary('places');
  const { AdvancedMarkerElement } = await google.maps.importLibrary('marker');

  const request = {
    locationRestriction: {
      center: clickedLocation,
      radius,
    },
    fields: ['displayName', 'location', 'businessStatus', 'types'],
    includedPrimaryTypes,
    maxResultCount: 20,
    rankPreference: SearchNearbyRankPreference.POPULARITY,
    language: 'en-AU',
  };

  const { places } = await Place.searchNearby(request);

  const markers = [];
  if (places.length) {
    places.forEach((place, index) => {
      const markerHTML = ReactDOMServer.renderToStaticMarkup(
        <CustomerMarker number={index + 1} />
      );
      const wrapper = document.createElement('div');
      wrapper.innerHTML = markerHTML;
      const marker = new AdvancedMarkerElement({
        map,
        position: place.location,
        title: place.displayName,
        content: wrapper.firstChild,
      });
      markers.push(marker);
    });
  }

  return { places, markers };
}
