const nearbySearch = (mapInstance, location, setPlaces) => {
  if (!mapInstance || !window.google || !window.google.maps || !window.google.maps.places) {
    console.error("Google Maps Places API not available or mapInstance is invalid");
    return;
  }

  const service = new window.google.maps.places.PlacesService(mapInstance); // ✅ 이게 정석입니다

  const request = {
    location,
    radius: 800,
    type: ['restaurant', 'convenience_store', 'cafe', 'supermarket'],
  };

  service.nearbySearch(request, (results, status) => {
    if (status === window.google.maps.places.PlacesServiceStatus.OK) {
      setPlaces(results);
    } else {
      console.error('Places search failed:', status);
    }
  });
};

export default nearbySearch;
