/* global google */
import { useEffect, useRef } from 'react';
import { Loader } from '@googlemaps/js-api-loader';

const useGoogleMap = (mapRef) => {
  const mapInstance = useRef(null);
  const markerInstance = useRef(null);
  const nearbyMarkersRef = useRef([]); // Ïù¥Ï†Ñ ÎßàÏª§Îì§ÏùÑ Ï†ÄÏû•Ìï¥ÏÑú ÏßÄÏö∞Í∏∞ ÏúÑÌï¥

  useEffect(() => {
    const loader = new Loader({
      apiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
      version: 'weekly',
      libraries: ['marker', 'places'],
    });

    loader.load().then(async () => {
      const { Map } = await window.google.maps;
      const { AdvancedMarkerElement } = await google.maps.importLibrary('marker');
      const { Place } = await google.maps.importLibrary('places');

      const melbourneCBD = { lat: -37.8136, lng: 144.9631 };

      const map = new Map(mapRef.current, {
        center: melbourneCBD,
        zoom: 14,
        mapId: 'feabc4fd011254f284cca2c8',
      });

      mapInstance.current = map;

      map.addListener('click', async (event) => {
        // ÎßàÏª§ ÏúÑÏπò Ïù¥Îèô ÎòêÎäî ÏÉùÏÑ±
        if (markerInstance.current) {
          markerInstance.current.position = {
            lat: event.latLng.lat(),
            lng: event.latLng.lng()
          };
        } else {
          const newMarker = new AdvancedMarkerElement({
            position: event.latLng,
            map,
          });
          markerInstance.current = newMarker;
        }

        console.log("marker pos:", markerInstance.current.position.lat, markerInstance.current.position.lng);

        // üîΩ Í∏∞Ï°¥ nearby ÎßàÏª§ ÏÇ≠Ï†ú
        nearbyMarkersRef.current.forEach((m) => m.map = null);
        nearbyMarkersRef.current = [];

        // ‚úÖ Ïó¨Í∏∞Í∞Ä ÌïµÏã¨! ÌÅ¥Î¶≠Îêú ÏúÑÏπò Ï£ºÎ≥Ä Ïû•ÏÜå Í≤ÄÏÉâ
        const placeService = new Place();

        try {
          const { places } = await placeService.searchNearby({
            locationRestriction: {
              center: {
                lat: event.latLng.lat(),
                lng: event.latLng.lng(),
              },
              radius: 800, // ÎèÑÎ≥¥ 10Î∂Ñ
            },
            includedPrimaryTypes: ['restaurant', 'cafe'],
            fields: ['id','displayName', 'location'],
            maxResultCount: 10,
            language: 'en',
            region: 'au',
          });

          if (places.length) {
            const bounds = new google.maps.LatLngBounds();

            places.forEach((place, index) => {
              if (!place || !place.id || !place.location || !place.displayName) {
                console.warn(`Skipping invalid place at index ${index}:`, place);
                return;
              }

              const nearbyMarker = new AdvancedMarkerElement({
                map,
                position: place.location,
                title: typeof place.displayName === 'string' ? place.displayName : '', // üîê title Î≥¥Ìò∏
              });

              nearbyMarkersRef.current.push(nearbyMarker);
              bounds.extend(place.location);
            });



            map.fitBounds(bounds);
          } else {
            console.log('No nearby places found.');
          }
        } catch (error) {
          console.error('Error during place search:', error);
        }
      });
    });
  }, [mapRef]);
};

export default useGoogleMap;
