import React, { useState, useRef } from 'react';
import { GoogleMap } from '@react-google-maps/api';
import useGoogleMapApi from '../../hooks/useGoogleMapAPI';
import LocationButton from './LocationButton';

const containerStyle = {
  width: '100%',
  height: '100vh',
};

const center = {
  lat: -37.8136,
  lng: 144.9631,
};

const MapView = () => {
  const { isLoaded, loadError } = useGoogleMapApi();
  const mapRef = useRef(null);
  const [selectedPosition, setSelectedPosition] = useState(null);
  const markersRef = useRef([]); //  毵旎 レ

  const handleMapLoad = (map) => {
    mapRef.current = map;
  };

  const handleMapClick = (event) => {
    const latLng = {
      lat: event.latLng.lat(),
      lng: event.latLng.lng(),
    };
    setSelectedPosition(latLng);

    // 旮办〈 毵旎 瓯
    markersRef.current.forEach(marker => marker.map = null);
    markersRef.current = [];
  };

  const handleSearchNearby = async () => {
    if (!selectedPosition || !mapRef.current) return;

    const { Place } = await window.google.maps.importLibrary('places');
    const { AdvancedMarkerElement } = await window.google.maps.importLibrary('marker');

    try {
      const results = await Place.searchNearby({
        location: selectedPosition,
        radius: 800,
        includedTypes: ['restaurant', 'cafe', 'convenience_store', 'supermarket'],
        maxResultCount: 20,
      });

      // 挫 毵旎 
      markersRef.current.forEach(marker => marker.map = null);
      markersRef.current = [];

      results.forEach((place) => {
        const marker = new AdvancedMarkerElement({
          map: mapRef.current,
          position: place.location,
          title: place.displayName?.text || 'Unnamed Place',
        });
        markersRef.current.push(marker);
      });

    } catch (error) {
      console.error('Place.searchNearby :', error);
    }
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading maps...</div>;

  return (
    <>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={14}
        onClick={handleMapClick}
        onLoad={handleMapLoad}
      />
      {selectedPosition && <LocationButton onClick={handleSearchNearby} />}
    </>
  );
};

export default MapView;
