/* global google */
import { useEffect, useRef } from 'react';
import { Loader } from '@googlemaps/js-api-loader';
import CustomerMarker from '../components/Map/ CustomMarker';
import ReactDOMServer from 'react-dom/server';


const useGoogleMap = (mapRef, onLocationSelect, onPlacesUpdate) => {
  const mapInstance = useRef(null);
  const markerInstance = useRef(null);
  const nearbyMarkersRef = useRef([]); // 기존 마커들 저장

  useEffect(() => {
    const loader = new Loader({
      apiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
      version: 'weekly',
    });

    loader.load().then(async () => {
      const { Map } = await google.maps.importLibrary('maps');
      const { AdvancedMarkerElement } = await google.maps.importLibrary('marker');
      const { Place, SearchNearbyRankPreference } = await google.maps.importLibrary('places');

      const melbourneCBD = { lat: -37.8136, lng: 144.9631 };
      const map = new Map(mapRef.current, {
        center: melbourneCBD,
        zoom: 14,
        mapId: 'feabc4fd011254f284cca2c8',
      });

      mapInstance.current = map;

      map.addListener('click', async (event) => {

      });
    });
  }, [mapRef, onLocationSelect, onPlacesUpdate]);
};

export default useGoogleMap;
