/* global google */
import ReactDOMServer from 'react-dom/server';
import CustomerMarker from '../components/Map/ CustomMarker';



function normalizeName(name) {
  return name.toLowerCase()
    .replace(/building\s*\d+/g, '')
    .replace(/academic|campus|centre|center|college|institute|school|university/g, '')
    .replace(/[^a-z0-9]/g, '')
    .trim();
}

function removeDuplicatePlacesByName(places) {
  const seen = new Set();
  return places.filter(place => {
    const norm = normalizeName(place.displayName);
    if ([...seen].some(existing => norm.includes(existing) || existing.includes(norm))) {
      return false;
    }
    seen.add(norm);
    return true;
  });
}

function toRad(deg) {
  return deg * Math.PI / 180;
}

function calculateDistance(loc1, loc2) {
  const R = 6371; // Earth radius in km

  const lat1 = typeof loc1.lat === 'function' ? loc1.lat() : loc1.lat;
  const lng1 = typeof loc1.lng === 'function' ? loc1.lng() : loc1.lng;
  const lat2 = typeof loc2.lat === 'function' ? loc2.lat() : loc2.lat;
  const lng2 = typeof loc2.lng === 'function' ? loc2.lng() : loc2.lng;

  const dLat = toRad(lat2 - lat1);
  const dLng = toRad(lng2 - lng1);
  const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
            Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) *
            Math.sin(dLng/2) * Math.sin(dLng/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  return R * c;
}

export async function searchNearbyAmenities({ map, clickedLocation, radius, includedPrimaryTypes }) {
  const { Place, SearchNearbyRankPreference } = await google.maps.importLibrary('places');
  const { AdvancedMarkerElement } = await google.maps.importLibrary('marker');

  const CATEGORY_TYPE_MAP = {
    education: ['primary_school', 'secondary_school', 'university'],
    medical: ['hospital', 'doctor', 'clinic'],
    grocery: ['supermarket', 'grocery_store', 'department_store', 'shopping_mall', 'asian_grocery'],
    transport: ['train_station', 'subway_station', 'bus_station', 'transit_station', 'light_rail_station'],
    green: ['park', 'state_park', 'campground', 'rv_park'],
    culture: ['museum', 'library', 'art_gallery', 'stadium', 'concert_hall'],
  };

  const categoryReverseLookup = {};
  for (const [category, types] of Object.entries(CATEGORY_TYPE_MAP)) {
    for (const type of types) {
      categoryReverseLookup[type] = category;
    }
  }

  const request = {
    locationRestriction: {
      center: clickedLocation,
      radius,
    },
    fields: ['displayName', 'location', 'businessStatus', 'types', 'rating', 'userRatingCount'],
    includedPrimaryTypes,
    maxResultCount: 20,
    rankPreference: SearchNearbyRankPreference.POPULARITY,
    language: 'en-AU',
  };

  const { places = [] } = await Place.searchNearby(request);

  const filteredPlaces = places.filter(place => {
    if (place.businessStatus !== 'OPERATIONAL') return false;
    const types = place.types ?? [];
    const name = place.displayName?.toLowerCase() ?? '';
    const isTransport = types.some(t => CATEGORY_TYPE_MAP.transport.includes(t));

    if (!isTransport) {
      if ((place.userRatingCount ?? 0) < 5) return false;
      if ((place.rating ?? 0) < 3) return false;
    }
    else{
      console.log("is transport!!")
    }


    if (types.some(t => CATEGORY_TYPE_MAP.education.includes(t))) return true;

    if ((types.includes('hospital') && name.includes('hospital')) ||
        (types.includes('clinic') && /specialist|surgery|medical|health/.test(name))) {
      return true;
    }

    if (types.includes('asian_grocery')) return true;

    if (types.includes('department_store') || types.includes('shopping_mall') || types.includes('outlet')) {
      return true;
    }

    if ((types.includes('supermarket') || types.includes('grocery_store')) &&
        (/coles|woolworths|aldi/.test(name) || (place.userRatingCount ?? 0) >= 50)) {
      return true;
    }

    if (types.some(t => CATEGORY_TYPE_MAP.transport.includes(t))) return true;

    if (types.some(t => CATEGORY_TYPE_MAP.green.includes(t)) && place.userRatingCount >= 10 && place.rating >= 3.8) {
      return true;
    }

    if (types.some(t => CATEGORY_TYPE_MAP.culture.includes(t)) && place.userRatingCount >= 10 && place.rating >= 3.8) {
      return true;
    }

    return false;
  });

  let uniquePlaces = removeDuplicatePlacesByName(filteredPlaces);

  uniquePlaces.forEach(place => {
    const matchedCategory = place.types?.map(t => categoryReverseLookup[t]).find(Boolean);
    place.category = matchedCategory || null;
  });

  // ðŸ“Œ transport ì¹´í…Œê³ ë¦¬ë§Œ ê±°ë¦¬ìˆœìœ¼ë¡œ ìž¬ì •ë ¬
  const transportPlaces = uniquePlaces
    .filter(p => p.category === 'transport')
    .sort((a, b) => {
      const distA = calculateDistance(clickedLocation, a.location);
      const distB = calculateDistance(clickedLocation, b.location);
      return distA - distB;

    });

  // ê·¸ ì™¸ ì¹´í…Œê³ ë¦¬ëŠ” ê¸°ì¡´ ìˆœì„œ ìœ ì§€
  const otherPlaces = uniquePlaces.filter(p => p.category !== 'transport');

  const finalPlaces = [...transportPlaces, ...otherPlaces];

  const markers = finalPlaces.map((place, index) => {
    const markerHTML = ReactDOMServer.renderToStaticMarkup(
      <CustomerMarker number={index + 1} />
    );
    const wrapper = document.createElement('div');
    wrapper.innerHTML = markerHTML;
    return new AdvancedMarkerElement({
      map,
      position: place.location,
      title: place.displayName,
      content: wrapper.firstChild,
    });
  });

  return { places: finalPlaces, markers };
}
