import React from "react";

const TYPE_CATEGORY_MAP = {
    // 교육
    school: 'education',
    university: 'education',
    college: 'education',

    // 식료품/생필품
    supermarket: 'grocery',
    grocery_store: 'grocery',

    // 교통
    train_station: 'transport',
    bus_station: 'transport',
    transit_station: 'transport',
    light_rail_station: 'transport',
    subway_station: 'transport',

    // 의료
    hospital: 'medical',
    pharmacy: 'medical',
    doctor: 'medical',
    dentist: 'medical',
    clinic: 'medical',

    // 공원/녹지
    park: 'green',
    state_park: 'green',
    natural_feature: 'green',


    // 문화시설
    museum: 'culture',
    tourist_attraction: 'culture',
    library: 'culture',
    art_gallery: 'culture',
    movie_theater: 'culture',
    theater: 'culture',
    concert_hall: 'culture',
    stadium: 'culture',

  };

function getCategoryFromTypes(types) {
    if (!types || types.length === 0) return 'other';

    for (const type of types) {
        const category = TYPE_CATEGORY_MAP[type];
        if (category) return category; // 가장 먼저 매칭되는 카테고리 반환
    }

    return 'other'; // 매칭 실패 시
}


// 위에서 만든 getCategoryFromTypes 함수와 TYPE_CATEGORY_MAP 포함

function Header({ selectedLocation, places }) {
  const coords = selectedLocation?.toJSON?.();

  return (
    <div className="absolute top-0 left-0 w-full z-20 bg-white p-4 shadow-md">

      <div>
        {coords
          ? `Selected location: (${coords.lat.toFixed(5)}, ${coords.lng.toFixed(5)})`
          : 'Click on the map to select a location.'
        }
      </div>
      <ul>
        {places.map((place, index) => {
          const category = getCategoryFromTypes(place.types);
          return (
            <li key={index}>
              {index + 1}: {place.displayName}, <strong>{category}</strong>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default Header;
