const nearbySearch = async (location, setPlaces) => {
  const { lat, lng } = location;
  const radius = 800; // 도보 10분 = 약 800m
  const type = 'restaurant|cafe|convenience_store|supermarket';

  const url = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${lat},${lng}&radius=${radius}&type=${type}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`;

  try {
    const response = await fetch(url);
    const data = await response.json();

    if (data.status === 'OK') {
      setPlaces(data.results);
    } else {
      console.error('Places search failed:', data.status);
    }
  } catch (err) {
    console.error('Fetch error:', err);
  }
};

export default nearbySearch;
