// src/components/Map/fetchNearbyPlaces.js
const fetchNearbyPlaces = async (location, setPlaces) => {
    const { lat, lng } = location;
    const radius = 800;
    const types = ['restaurant', 'cafe', 'convenience_store', 'supermarket'];

    const results = [];

    for (const type of types) {
      const url = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${lat},${lng}&radius=${radius}&type=${type}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`;

      try {
        const response = await fetch(url);
        const data = await response.json();

        if (data.status === 'OK') {
          results.push(...data.results);
        } else {
          console.warn(`${type} 검색 실패:`, data.status);
        }
      } catch (err) {
        console.error('Fetch error:', err);
      }
    }

    setPlaces(results);
  };

  export default fetchNearbyPlaces;
