import React, { useState, useRef } from 'react';
import { GoogleMap } from '@react-google-maps/api';
import LocationButton from './LocationButton';
import useGoogleMapApi from '../../hooks/useGoogleMapAPI';

const containerStyle = {
  width: '100%',
  height: '100vh',
};

const center = {
  lat: -37.8136,
  lng: 144.9631,
};

const MapView = () => {
  const { isLoaded, loadError } = useGoogleMapApi();
  const mapRef = useRef(null);
  const markersRef = useRef([]);
  const [selectedPosition, setSelectedPosition] = useState(null);

  const handleMapLoad = (map) => {
    mapRef.current = map;
  };

  const handleMapClick = (event) => {
    const latLng = {
      lat: event.latLng.lat(),
      lng: event.latLng.lng(),
    };
    setSelectedPosition(latLng);

    // 기존 마커 제거
    markersRef.current.forEach((marker) => marker.setMap(null));
    markersRef.current = [];

    // 클릭한 위치에 마커 추가
    const marker = new window.google.maps.Marker({
      position: latLng,
      map: mapRef.current,
      title: 'Selected Location',
    });
    markersRef.current.push(marker);
  };

  const handleSearchNearby = async () => {
    // if (!selectedPosition || !mapRef.current) return;

    // const service = new window.google.maps.places.PlacesService(mapRef.current);

    // const request = {
    //   location: selectedPosition,
    //   radius: 800,
    //   type: ['restaurant', 'cafe', 'convenience_store', 'supermarket'],
    // };

    // service.nearbySearch(request, (results, status) => {
    //   if (status === window.google.maps.places.PlacesServiceStatus.OK) {
    //     // 기존 마커 제거
    //     markersRef.current.forEach((marker) => marker.setMap(null));
    //     markersRef.current = [];

    //     results.forEach((place) => {
    //       const marker = new window.google.maps.Marker({
    //         map: mapRef.current,
    //         position: place.geometry.location,
    //         title: place.name,
    //       });
    //       markersRef.current.push(marker);
    //     });
    //   } else {
    //     console.error('Nearby search failed:', status);
    //   }
    // });
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading maps...</div>;

  return (
    <>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={14}
        onClick={handleMapClick}
        onLoad={handleMapLoad}
      />
      {selectedPosition && <LocationButton onClick={handleSearchNearby} />}
    </>
  );
};

export default MapView;
