/* global google */
import { useEffect, useRef } from 'react';
import { Loader } from '@googlemaps/js-api-loader';

const useGoogleMap = (mapRef) => {
  const mapInstance = useRef(null);
  const markerInstance = useRef(null);


  useEffect(() => {
    const loader = new Loader({
      apiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
      version: 'weekly',
      libraries: ['marker'],
    });

    loader.load().then(async () => {
      const { Map } = await window.google.maps;
      const { AdvancedMarkerElement } = await google.maps.importLibrary('marker');

      const melbourneCBD = { lat: -37.8136, lng: 144.9631 };

      const map = new Map(mapRef.current, {
        center: melbourneCBD,
        zoom: 14,
        mapId: 'feabc4fd011254f284cca2c8',
      });

      mapInstance.current = map;

      map.addListener('click', (event) => {
        if (markerInstance.current) {
          markerInstance.current.setPosition(event.latLng);
        }
        else {
          const newMarker = new AdvancedMarkerElement({
            position: event.latLng,
            map,
          });
          markerInstance.current = newMarker;
        }
      });
    });
  }, [mapRef]);
};

export default useGoogleMap;
