import React from "react";

const TYPE_CATEGORY_MAP = {
  school: 'education',
  university: 'education',
  college: 'education',

  supermarket: 'grocery',
  grocery_store: 'grocery',

  train_station: 'transport',
  bus_station: 'transport',
  transit_station: 'transport',
  light_rail_station: 'transport',
  subway_station: 'transport',

  hospital: 'medical',

  park: 'green',
  state_park: 'green',
  natural_feature: 'green',

  museum: 'culture',
  tourist_attraction: 'culture',
  library: 'culture',
  art_gallery: 'culture',
  movie_theater: 'culture',
  theater: 'culture',
  concert_hall: 'culture',
  stadium: 'culture',
};

const CATEGORY_TYPES_MAP = {
    education: ['school', 'university', 'college'],
    grocery: ['supermarket', 'grocery_store'],
    transport: ['train_station', 'bus_station', 'transit_station', 'light_rail_station', 'subway_station'],
    medical: ['hospital'],
    green: ['park', 'state_park', 'natural_feature'],
    culture: ['museum', 'tourist_attraction', 'library', 'art_gallery', 'movie_theater', 'theater', 'concert_hall', 'stadium'],
  };

function getCategoryFromTypes(types) {
  if (!types || types.length === 0) return 'other';
  for (const type of types) {
    const category = TYPE_CATEGORY_MAP[type];
    if (category) return category;
  }
  return 'other';
}
function Header({ selectedLocation, places, selectedCategories, onCategoryChange }) {
    const coords = selectedLocation?.toJSON?.();

    return (
      <div className="absolute top-0 left-0 w-full z-20 bg-white bg-opacity-90 p-4 shadow-md max-h-[40vh] overflow-y-auto">
        <div className="font-semibold mb-2">
          {coords
            ? `Selected location: (${coords.lat.toFixed(5)}, ${coords.lng.toFixed(5)})`
            : 'Click on the map to select a location.'}
        </div>

        <div className="flex flex-wrap gap-2 mb-2 text-sm">
          <label><input type="checkbox" checked={selectedCategories.includes('all')} onChange={() => onCategoryChange('all')} /> All</label>
          {Object.keys(CATEGORY_TYPES_MAP).map(cat => (
            <label key={cat}>
              <input
                type="checkbox"
                checked={selectedCategories.includes(cat)}
                onChange={() => onCategoryChange(cat)}
              /> {cat}
            </label>
          ))}
        </div>

        <ul className="text-sm max-h-[200px] overflow-y-auto">
          {places.map((place, index) => {
            const category = getCategoryFromTypes(place.types);
            return (
              <li key={index} className="mb-1">
                {index + 1}: {place.displayName}, <strong>{category}</strong>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }


export default Header;
