import React, { useState } from 'react';

const CategoryFilter = ({ onChange, activeCategories }) => {
  const categories = ['all', 'education', 'grocery', 'transport', 'medical', 'green', 'culture', 'other'];

  const handleCheckboxChange = (category) => {
    let updated;

    if (category === 'all') {
      updated = ['all'];
    } else {
      if (activeCategories.includes(category)) {
        updated = activeCategories.filter((c) => c !== category);
      } else {
        updated = [...activeCategories.filter(c => c !== 'all'), category];
      }

      if (updated.length === 0) updated = ['all'];
    }

    onChange(updated);
  };

  return (
    <div className="flex flex-wrap gap-4 p-4 bg-white shadow-md rounded">
      {categories.map((category) => (
        <label key={category} className="flex items-center gap-2 capitalize">
          <input
            type="checkbox"
            value={category}
            checked={activeCategories.includes(category)}
            onChange={() => handleCheckboxChange(category)}
          />
          {category}
        </label>
      ))}
    </div>
  );
};


export default CategoryFilter;
